(825, 6202, '', '', '', '', '', '', '', '', '', '', '', 'TEXT', '', '', '', 'Dropbox Manual (file list)', '', 'NOT NULL', 1, 6, '\"<field \\r\\n\\ttype=\\\"dropboxfiles\\\" \\r\\n\\tname=\\\"manual_files\\\" \\r\\n\\tlabel=\\\"Files\\\" \\r\\n\\tdescription=\\\"Select the files you would like to add to this sermon.\\\" \\r\\n\\tclass=\\\"list_class\\\" \\r\\n\\tmultiple=\\\"true\\\" \\r\\n\\tdefault=\\\"0\\\" \\r\\n\\trequired=\\\"true\\\" \\r\\n\\treadonly=\\\"\\\" \\r\\n\\tdisabled=\\\"\\\" \\r\\n\\textends=\\\"list\\\" \\r\\n\\tbutton=\\\"false\\\" \\r\\n\\ttype_php_1=\\\"\\/\\/ set the default\\r\\n\\t\\t$options[] = JHtml::_(\'select.option\', \'\', JText::_(\'The local listing of the Manual Dropbox folder is empty.\'));\\r\\n\\t\\t$links = ###Component###Helper::getDropboxLink(\'manual\',2);\\r\\n\\t\\tif (###Component###Helper::checkArray($links))\\r\\n\\t\\t{\\r\\n\\t\\t\\t$options = array();\\r\\n\\t\\t\\tforeach ($links as $file => $link)\\r\\n\\t\\t\\t{\\r\\n\\t\\t\\t\\t$name = substr($file, strrpos($file, \'\\/\') + 1);\\r\\n\\t\\t\\t\\t$options[] = JHtml::_(\'select.option\', $file, $name);\\r\\n\\t\\t\\t}\\r\\n\\t\\t}\\r\\n\\t\\treturn $options;\\\" \\r\\n\\/>\"', '', 1, 450, 450, '2015-10-29 17:30:12', '2015-11-21 01:04:02', '', '0000-00-00 00:00:00', 11, '', 1, 628),
(826, 6203, '', '', '', '', '', '', '', '', '', '', '', 'TEXT', '', '', '', 'Local (file list)', '', 'NOT NULL', 1, 6, '\"<field \\r\\n\\ttype=\\\"localfiles\\\" \\r\\n\\tname=\\\"local_files\\\" \\r\\n\\tlabel=\\\"Files\\\" \\r\\n\\tdescription=\\\"Select the files you would like to add to this sermon.\\\" \\r\\n\\tclass=\\\"list_class\\\" \\r\\n\\tmultiple=\\\"true\\\" \\r\\n\\tdefault=\\\"0\\\" \\r\\n\\trequired=\\\"true\\\" \\r\\n\\treadonly=\\\"\\\" \\r\\n\\tdisabled=\\\"\\\" \\r\\n\\textends=\\\"list\\\" \\r\\n\\tbutton=\\\"false\\\" \\r\\n\\ttype_php_1=\\\"\\/\\/ get local folder\\r\\n\\t\\t$localfolder = JComponentHelper::getParams(\'com_###component###\')->get(\'localfolder\', JPATH_ROOT.\'\\/images\');\\r\\n\\t\\t\\/\\/ set the default\\r\\n\\t\\t$options[] = JHtml::_(\'select.option\', \'\', JText::sprintf(\'Please add files to (%s)\',$localfolder));\\r\\n\\t\\t\\/\\/ inport all needed classes\\r\\n\\t\\tjimport(\'joomla.filesystem.file\');\\r\\n\\t\\tjimport(\'joomla.filesystem.folder\');\\r\\n\\t\\t\\/\\/ setup the folder if it does not exist\\r\\n\\t\\tif (!JFolder::exists($localfolder))\\r\\n\\t\\t{\\r\\n\\t\\t\\tJFolder::create($localfolder);\\r\\n\\t\\t}\\r\\n\\t\\t\\/\\/ now check if there are files in the folder\\r\\n\\t\\tif ($files = JFolder::files($localfolder))\\r\\n\\t\\t{\\r\\n\\t\\t\\t$options = array();\\r\\n\\t\\t\\tforeach ($files as $file)\\r\\n\\t\\t\\t{\\r\\n\\t\\t\\t\\t$options[] = JHtml::_(\'select.option\', $file, $file);\\r\\n\\t\\t\\t}\\r\\n\\t\\t}\\r\\n\\t\\treturn $options;\\\" \\r\\n\\/>\"', '', 1, 450, 450, '2015-10-29 17:32:08', '2015-10-31 16:46:53', '', '0000-00-00 00:00:00', 5, '', 1, 628),
